<?php/** * Created by PhpStorm. * User: asusa * Date: 2018/3/29/0029 * Author: Cary.He * Contact QQ  : 373889161($S$-Memory) * email: 373889161@qq.com * Time: 09:36 */namespace backend\controllers;use Yii;use backend\core\MY_Controller;use backend\models\Admin;use yii\data\Pagination;class AdminController extends MY_Controller{    /**     * @return string     * 管理员列表     */    public function actionIndex()    {        $query = Admin::find();        //设置分页        $pagination = new Pagination([            'defaultPageSize' => 10,//设置显示数量            'totalCount' => $query->count(),//总记录            'pageParam' => 'p', //URL参数设置为p            //'route' => false,  //隐藏路由            'validatePage' => false,        ]);        $countries = $query->orderBy('id desc')            ->offset($pagination->offset)            ->limit($pagination->limit)            ->all();        return $this->render('index', [            'pagination' => $pagination,            'countries' => $countries,        ]);    }    /**     * @return string|\yii\web\Response     * 添加管理员     */    public function actionCreate()    {        $model = new Admin();        $pass = Yii::$app->request->post('password2');        if ($model->load(Yii::$app->request->post()))        {            if($pass === $model->password)            {                Yii::$app->session->setFlash('success','添加成功！');                $model->signup();                return $this->redirect(['admin/']);            }            else            {                Yii::$app->session->setFlash('success','密码不一致！');                return $this->render('create', [                    'model' => $model,                ]);            }        } else {            return $this->render('create', [                'model' => $model,            ]);        }    }    /**     * @param $id     * @return string|\yii\web\Response     * 更新管理员信息     */    public function actionUpdate($id)    {        $model = $this->findAdmin($id);        $update = new Admin();        $pass2 = Yii::$app->request->post('password2');        if ($update->load(Yii::$app->request->post()))        {            if($pass2 === $update->password)            {                $update->upsave($id);                Yii::$app->session->setFlash('success','更新成功');                return $this->redirect(['index']);            }            else            {                Yii::$app->session->setFlash('success','密码不一致！');                return $this->render('update', [                    'model' => $model,                ]);            }        }        else        {            return $this->render('update', [                'model' => $model,            ]);        }    }    /**     * @param $id     * @return \yii\web\Response     * @throws \backend\core\NotFoundHttpException     * 删除管理员信息     */    public function actionDelete($id)    {        $this->findAdmin($id)->delete();        return $this->redirect(['index']);    }}