<?php/** * Created by PhpStorm. * User: asusa * Date: 2018/3/30/0030 * Author: Cary.He * Contact QQ  : 373889161($S$-Memory) * email: 373889161@qq.com * Time: 11:27 */namespace backend\controllers;use Yii;use backend\core\MY_Controller;use backend\models\Category;use yii\helpers\ArrayHelper;class CategoryController extends MY_Controller{    /**     * @return string     * 商品分类列表     */    public function actionIndex()    {        $fun_help = new \Helpers();        $countries=Category::find()->all();        $menu = $fun_help->gettree($countries,'parent_id','cat_id','cat_name',1);        $nav = $fun_help->formenu($menu);        return $this->render('index', [            'nav'       => $nav,        ]);    }    /**     * @return string|\yii\web\Response     * @throws \yii\base\Exception     * 添加商品分类     */    public function actionCreate()    {        $model = new Category();        $fun_help = new \Helpers();        $model->scenario = 'create';    //设置 create 场景验证        $countries=Category::find()->all();        $menu = $fun_help->gettree($countries);        $nav = $fun_help->formenu($menu);        $listData=ArrayHelper::map($nav,'id','name'); //输出选择上级分类        if ($model->load(Yii::$app->request->post()) &&  $model->signup()) {            Yii::$app->session->setFlash('success','添加成功!');            return $this->redirect('index');  //跳转到提示页面        } else {            return $this->render('create', [                'model' => $model,                'listData' => $listData,            ]);        }    }    /**     * @param $id     * @return string|\yii\web\Response     * @throws \backend\core\NotFoundHttpException     * 编辑商品分类     */    public function actionEdit($id)    {        $model = $this->findCategory($id);        if(!$model)        {            return $this->redirect('index');        }        $model->scenario = 'create';   //调用验证场景        $help = new \Helpers();        if($model->load(Yii::$app->request->post())){            $sel_id = $help->getarr($id,1);            $new_arr = explode(',',$help->returnid($sel_id));            if(in_array($model->parent_id,$new_arr))            {                Yii::$app->session->setFlash('success','选择上级分类错误!');            }            else            {                $model->save();                Yii::$app->session->setFlash('success','更新成功!');                return $this->redirect('index');            }        }        $countries=Category::find()->all();        $menu = $help->gettree($countries);        $nav = $help->formenu($menu);        $listData=ArrayHelper::map($nav,'id','name'); //输出选择上级分类        return $this->render('edit',['model'=>$model,'listData'=>$listData]);    }    /**     * @param $id     * @return \yii\web\Response     * @throws NotFoundHttpException     * 删除商品分类     */    public function actionDelete($id)    {        $goods = Category::find()            ->leftJoin('yii_goods', 'yii_goods.cat_id = yii_category.cat_id')            ->where(['yii_goods.cat_id'=>$id])            ->all();        $help = new \Helpers();        $carr = $help->getarr($id);        $model = new Category();        $model->stuts($goods,$carr,$id);        return $this->redirect(['index']);    }}