<?php/** * Created by PhpStorm. * User: asusa * Date: 2018/3/29/0029 * Author: Cary.He * Contact QQ  : 373889161($S$-Memory) * email: 373889161@qq.com * Time: 15:57 */namespace backend\controllers;use backend\core\MY_Controller;use backend\models\Contact;class ContactController extends MY_Controller{    /**     * @return string     * 留言板列表     */    public function actionIndex()    {        $query = Contact::find();        //设置分页        $pagination = $this->Page($query);        $countries = $query->orderBy('id desc')            ->offset($pagination->offset)            ->limit($pagination->limit)            ->all();        return $this->render('index', [            'pagination' => $pagination,            'countries' => $countries,        ]);    }    /**     * @param $id     * @return \yii\web\Response     * 删除留言     */    public function actionDelete($id)    {        $this->findContact($id)->delete();        return $this->redirect(['index']);    }    /**     * @param $id     * @return string     * 查看留言板信息     */    public function actionShow($id)    {        $model = $this->findContact($id);        return $this->render('show',['model'=>$model]);    }}