<?php/** * Created by PhpStorm. * User: asusa * Date: 2018/3/27/0027 * Author: Cary.He * Contact QQ  : 373889161($S$-Memory) * email: 373889161@qq.com * Time: 14:04 */namespace backend\controllers;use Yii;use backend\core\MY_Controller;use backend\models\Admin;class IndexController extends MY_Controller{    public function actionIndex()    {        $model = new Admin();        $connection  = Yii::$app->db;        $sql     = "SELECT VERSION() as mysql_version";        $command = $connection->createCommand($sql);        $version     = $command->queryScalar($sql);        return $this->render('index',[            'model' => $model,            'version' => $version,        ]);    }    /**     * @return \yii\web\Response     * 安全退出登录     */    public function actionLog_ut(){        // 销毁session中所有已注册的数据        //$session->destroy();        //销毁某一个session        unset(Yii::$app->session['SESSION_NAME']);        return $this->redirect('/login');    }}