<?php/** * Created by PhpStorm. * User: asusa * Date: 2018/3/28/0028 * Author: Cary.He * Contact QQ  : 373889161($S$-Memory) * email: 373889161@qq.com * Time: 10:23 *//** * @return string|\yii\web\Response * 登录界面 */namespace backend\controllers;use Yii;use backend\models\Admin;use yii\web\Controller;class LoginController extends Controller{    public function actionIndex()    {        if(\Helpers::resession('SESSION_NAME'))        {            return $this->redirect('index');            //echo '登录成功！';        }        $model = new Admin();        if ($model->load(Yii::$app->request->post()) && $model->login())        {            // 生成登录session            \Helpers::yiisession(Yii::$app->user->identity->id,'SESSION_NAME');            // 登录成功跳到后台首页            return $this->redirect('/');        }        else        {            return $this->renderPartial('login',[                'model' => $model,            ]);        }    }}