<?php/** * Created by PhpStorm. * User: asusa * Date: 2018/3/27/0027 * Author: Cary.He * Contact QQ  : 373889161($S$-Memory) * email: 373889161@qq.com * Time: 10:29 */namespace frontend\controllers;use Yii;use frontend\core\MY_Controller;use frontend\models\Contact;class ContactController extends MY_Controller{    /**     * @return string     * 留言板     */    public function actionIndex()    {        $model = new Contact();        return $this->render('index',['model'=>$model]);    }    /**     * @return \yii\web\Response     * 执行添加留言操作     */    public function actionAddmes()    {        $model = new Contact();        $model->load(Yii::$app->request->post());        $model->addmes();        return $this->redirect('index');    }}