<?php/** * Created by PhpStorm. * User: asusa * Date: 2018/3/27/0027 * Author: Cary.He * Contact QQ  : 373889161($S$-Memory) * email: 373889161@qq.com * Time: 10:57 */namespace frontend\controllers;use frontend\core\MY_Controller;use frontend\models\Category;class GoodsController extends MY_Controller{    public function actionIndex()    {        //åˆ†ç±»        $category = Category::find()            ->leftJoin('yii_goods','yii_goods.cat_id=yii_category.cat_id')            ->where('yii_goods.cat_id = yii_category.cat_id AND yii_category.index_show=1 AND yii_category.is_show=1')            ->all();        $jiukj = $this->goodsshow(27,15);        $ershi = $this->goodsshow(28,15);        $nvzhuang = $this->goodsshow(13,15);        $nanzhuang = $this->goodsshow(14,15);        $select = 1;        $header = $this->renderpartial('/layouts/goods_header',['select' => $select]);        return $this->render('index', [            'category' => $category,            'jiukj' => $jiukj,            'ershi' => $ershi,            'nvzhuang' => $nvzhuang,            'nanzhuang' => $nanzhuang,            'web_header' => $header,        ]);    }}